#include <bits/stdc++.h>
using namespace std;using ll=long long;ll M=1e4;using m=array<array<ll,3>,3>;m l(m a,m b){m c;for(int i=0;i<3;++i)for(int j=0;j<3;++j)c[i][j]=0;for(int i=0;i<3;++i){for(int j=0;j<3;++j){for(int k=0;k<3;++k){c[i][j]+=a[i][k]*b[k][j];c[i][j]%=M;}}}return c;}m f(m a,int n){if(!n)return{array<ll,3>{1,0,0},array<ll,3>{0,1,0},array<ll,3>{0,0,1}};if(n==1)return a;m r=f(a,n/2);r=l(r,r);if(n&1)r=l(a,r);return r;}ll s(ll n){if(n<=4){return(ll[]){1,2,6,19}[n-1];}m g={array<ll,3>{5,M-7,4},array<ll,3>{1,0,0},array<ll,3>{0,1,0}};g=f(g,n-4);return(19*g[0][0]+6*g[0][1]+2*g[0][2])%M;}int main(){int t;scanf("%d",&t);for(ll tc=1;tc<=t;++tc){int n;scanf("%d",&n);printf("Case %lld: %lld\n",tc,s(n));}}
